def create_mentor name,lastname, age, description, email, business, facebook = "#", twitter = "#", instagram = "#", image = 'avatar1.png'
  Mentor.create(
      name: name,
      lastname: lastname,
      age: age,
      description: description,
      email: email,
      business: business,
      facebook: facebook,
      twitter: twitter,
      instagram: instagram,
      avatar: File.new("#{Rails.root}/app/assets/images/#{image}")
  )
end

def create_ally name, image = 'avatar1.png'
  Ally.create(
      name: name,
      image: File.new("#{Rails.root}/app/assets/images/#{image}")
  )
end

